import { Sequelize } from "sequelize"
import db from "../models"
import InsertProductRequest from "../dtos/requests/product/InsertProductRequest"
import UpdateProductRequest from "../dtos/requests/product/UpdateProductRequest"
const{Op} = Sequelize
export async function getProducts(req, res) {
    // const products = await db.Product.findAll()
    //search and paging
    const {search = '', page = 1} = req.query
    const pageSize = 5
    const offset = (page-1) * pageSize// trang 2 bat dau tu, bo qua trang truoc
    let whereClause = {};
    if (search.trim() !== '') {
        whereClause = {
            [Op.or]: [
                { name: { [Op.like]: `%${search}%` } },
                { description: { [Op.like]: `%${search}%` } },
                { specification: { [Op.like]: `%${search}%` } }
            ]
        };
    }
    const [products, totalProducts] = await Promise.all([
        db.Product.findAll({
            where: whereClause,
            limit: pageSize,
            offset: offset,
        }),
        db.Product.count({
            where: whereClause
        })
    ])
    return res.status(200).json({
        message: 'Ly danh sch sn phm thnh cng',
        data: products,
        currentPage: parseInt(page, 10),
        totalPages: Math.ceil(totalProducts / pageSize),
        totalProducts
    })

}
export async function getProductById(req, res) {
    const {id} = req.params
    const product = await db.Product.findByPk(id)

    if(!product) {
        return res.status(404).json({
        message: 'Sn phm khng tm thy'
        })
    }

    res.status(200).json({
        message: 'Ly thng tin sn phm thnh cng',
        data: product
    })

}
export async function insertProduct(req, res) {
    
    const product = await db.Product.create(req.body)
    return res.status(501).json({
            message: "Thm sn phm thnh cng",
            data: product
        });

}
export async function deleteProduct(req, res) {
    const {id} = req.params
    const deleted = await db.Product.destroy({
        where: {id}
    })
    if(deleted) {
        return res.status(200).json({
        message: 'Xa sn phm thnh cng'
        })
    } else {
        return res.status(404).json({
        message: 'Sn phm khng tm thy'
        })
    }
    

}
export async function updateProduct(req, res) {
    const { id } = req.params;
    const updatedProduct = await db.Product.update(req.body, {
        where: { id }
    });
    if (updatedProduct[0] > 0) { 
        return res.status(200).json({
            message: 'Cp nht sn phm thnh cng'
        });
    } else {
        return res.status(404).json({
            message: 'Sn phm khng tm thy'
        });
    }
}


