sourcesContent:
import { Sequelize } from "sequelize"
import db from "../models"
import { Op } from 'sequelize';


export async function getBrands(req, res) {
    // Ly tham s search v paging t query
    const { search = '', page = 1 } = req.query;
    const pageSize = 5; 
    const offset = (page - 1) * pageSize;

    let whereClause = {};
    if (search.trim() !== '') {
        whereClause = {
            [Op.or]: [
                { name: { [Op.like]: `%${search}%` } },
            ]
        };
    }


    const [brands, totalBrands] = await Promise.all([
        db.Brand.findAll({ 
            where: whereClause,
            limit: pageSize,
            offset: offset,
        }),
        db.Brand.count({ 
            where: whereClause
        })
    ]);

    
    return res.status(200).json({
        message: 'Ly danh sch thng hiu thnh cng',
        data: brands, // Thay i data
        currentPage: parseInt(page, 10),
        totalPages: Math.ceil(totalBrands / pageSize),
        totalBrands 
    })
}

// export async function getBrands(req, res) {
//     const brands = await db.Brand.findAll()
//     res.status(200).json({
//         message: 'Ly danh sch thng hiu thnh cng',
//         data: brands
//     });
// }

export async function getBrandById(req, res) {
    const {id} = req.params
    const brand = await db.Brand.findByPk(id)
    
    if(!brand) {
        return res.status(404).json({
            message: 'Thng hiu khng tm thy'
        })
    }
    res.status(200).json({
        message: 'Ly thng tin thng hiu thnh cng',
        data: brand
    });
}

export async function insertBrand(req, res) {
    try {
        console.log(JSON.stringify(req.body));
        
        // S dng model 'Brand'
        const brand = await db.Brand.create(req.body);
        
        res.status(201).json({
            message: 'Thm mi thng hiu thnh cng',
            data: brand
        });
    } catch (error) {
        res.status(500).json({
            message: "Li khi thm thng hiu mi",
            error: error.message
        });
    }
}

export async function updateBrand(req, res) {
    res.status(200).json({
        message: 'Update thng hiu thnh cng'
    });
}

export async function deleteBrand(req, res) {
    res.status(200).json({
        message: 'Xa thng hiu thnh cng'
    });
}

