import { Sequelize } from "sequelize"
import { Op } from 'sequelize';
import db from "../models"
// export async function getCategories(req, res) {
//     const categories = await db.Category.findAll()
//     res.status(200).json({
//         message: 'Ly danh sch danh mc thnh cng',
//         data: categories
//     });
// }



export async function getCategories(req, res) {
    // Ly tham s search v paging t query
    const { search = '', page = 1 } = req.query;
    const pageSize = 5;
    const offset = (page - 1) * pageSize;

    let whereClause = {};
    if (search.trim() !== '') {
        whereClause = {
            [Op.or]: [
                { name: { [Op.like]: `%${search}%` } },
                //{ description: { [Op.like]: `%${search}%` } } 
            ]
        };
    }

    // Dng Promise.all  ly d liu v m tng s lng song song
    const [categories, totalCategories] = await Promise.all([
        db.Category.findAll({
            where: whereClause,
            limit: pageSize,
            offset: offset,
        }),
        db.Category.count({
            where: whereClause
        })
    ]);

    // Tr v response
    res.status(200).json({
        message: 'Ly danh sch danh mc thnh cng', // Thay i message
        data: categories, // Thay i data
        currentPage: parseInt(page, 10),
        totalPages: Math.ceil(totalCategories / pageSize), // Thay i total
        totalCategories // Thay i total
    });
}
export async function getCategoryById(req, res) {
    const {id} = req.params
    const category = await db.Category.findByPk(id)
    
    if(!category) {
        return res.status(404).json({
            message: 'Danh mc khng tm thy'
        })
    }
    res.status(200).json({
        message: 'Ly thng tin danh mc thnh cng',
        data: category
    });
}

export async function insertCategory(req, res) {
        const category = await db.Category.create(req.body);
        res.status(201).json({
            message: 'Thm mi danh mc thnh cng',
            data: category
        });
    
}
export async function deleteCategory(req, res) {
    res.status(200).json({
        message: 'Xa danh mc thnh cng'
    });
}

export async function updateCategory(req, res) {
    res.status(200).json({
        message: 'Update danh mc thnh cng'
    });
}

