      // define association here
      Brand.hasMany(models.Product,{
        foreignKey: 'brand_id',
      })
    }
  }
  Brand.init({
    name: DataTypes.STRING,
    image: DataTypes.TEXT
  }, {
    sequelize,
    modelName: 'Brand',
    tableName: 'brands',
    underscored: true
  });
  return Brand;
};

